import React from "react";
import "./Home.css";

const Home = () => {
  const cars = [
    { id: 1, name: "Voiture 1", model: "Modèle A", image: "cars1.jpg" },
    { id: 2, name: "Voiture 2", model: "Modèle B", image: "cars2.jpeg" },
    { id: 3, name: "Voiture 3", model: "Modèle C", image: "car3.jpg" },
  ];

  return (
    <div className="container">
      <h1 className="heading">Bienvenue sur notre service de location de voitures</h1>
      <p className="paragraph">
        Découvrez notre large sélection de voitures et trouvez celle qui
        correspond à vos besoins.
      </p>

      <h2 className="subHeading">Voitures Disponibles</h2>
      <ul className="list">
        {cars.map((car) => (
          <li key={car.id} className="listItem">
            <img
              src={require(`../images/${car.image}`).default}
              alt={car.name}
              className="image"
            />
            {car.name} - {car.model}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
