import React from "react";
import "./Header.css"; // Importez votre fichier de styles

const Header = ({ history, handleSubmit }) => {
  // Fonction pour gérer la connexion
  const handleLogin = () => {
    // Ajoutez ici la logique pour la connexion, par exemple, en redirigeant vers la page de connexion
    console.log("Connexion...");
  };

  return (
    <header className="main-header">
      <nav>
        <ul className="nav-list">
          <li className="nav-item">
            <a href="/">Accueil</a>
          </li>
          <li className="nav-item">
            <a href="/voitures">Voitures</a>
          </li>
          <li className="nav-item">
            <a href="/reservation">Réservation</a>
          </li>
        </ul>
      </nav>
      <div className="search-bar">
        <input type="text" placeholder="Rechercher une voiture" />
        <button onClick={handleSubmit}>Rechercher</button>
      </div>
      {/* Bouton de connexion */}
      <button className="login-button" onClick={handleLogin}>Connexion</button>
    </header>
  );
};

export default Header;
