import React, { useState } from "react";
import "./Messages.css";

const Messages = () => {
  const [messages, setMessages] = useState([
    { id: 1, text: "Salut!", sender: "user" },
    { id: 2, text: "Bonjour!", sender: "bot" },
  ]);

  const [currentMessage, setCurrentMessage] = useState("");

  const handleSendMessage = () => {
    if (currentMessage.trim() !== "") {
      const newMessage = {
        id: messages.length + 1,
        text: currentMessage,
        sender: "user",
      };

      setMessages([...messages, newMessage]);
      setCurrentMessage("");
    }
  };

  return (
    <div className="chat-container">
      <div className="sidebar">
        <div className="other-user">User 1</div>
        <div className="other-user">User 2</div>
      </div>
      <h3 id="titre">Discussion</h3>
      <div className="message-list">
        {messages.map((message) => (
          <div
          key={message.id}
          className={message.sender === "user" ? "user-message" : "autre-message"}
        >
          <p>
            <strong>{message.sender === "user" ? "Vous" : "autre"}:</strong>{" "}
            {message.text}
          </p>
        </div>
        ))}
        <div className="input-container">
        <input
          type="text"
          placeholder="Saisissez votre message..."
          value={currentMessage}
          onChange={(e) => setCurrentMessage(e.target.value)}
        />
        <button onClick={handleSendMessage}>Envoyer</button>
      </div>
      </div>
    </div>
  );
};

export default Messages;
