import React, { useState } from "react";
import "./Header.css"; // Importez votre fichier de styles

const Header = ({ history, handleSubmit }) => {
  const [showLogin, setShowLogin] = useState(false);

  // Fonction pour gérer l'ouverture de la fenêtre de connexion
  const handleLogin = () => {
    setShowLogin(true);
    document.body.classList.add("blur");
  };

  // Fonction pour gérer la fermeture de la fenêtre de connexion
  const closeLogin = () => {
    setShowLogin(false);
    document.body.classList.remove("blur");
  };

  return (
    <header className="main-header">
      <nav>
        <ul className="nav-list">
          <li className="nav-item">
            <a href="/">Accueil</a>
          </li>
          <li className="nav-item">
            <a href="/voitures">Voitures</a>
          </li>
          <li className="nav-item">
            <a href="/reservation">Réservation</a>
          </li>
        </ul>
      </nav>
      <div className="search-bar">
        <input type="text" placeholder="Rechercher une voiture" />
        <button onClick={handleSubmit}>Rechercher</button>
      </div>
      {/* Bouton de connexion */}
      <button className="login-button" onClick={handleLogin}>
        Connexion
      </button>

      {/* Fenêtre de connexion */}
      {showLogin && (
        <div className="login-modal">
          <span className="close-button" onClick={closeLogin}>
            &times;
          </span>
          <h2>Connexion</h2>
          <form>
          <p>
            <label htmlFor="username">Nom d'utilisateur:</label>
            <input type="text" id="username" name="username" />
          </p>
          <p>
            <label htmlFor="password">Mot de passe:</label>
           <input type="password" id="password" name="password" />
           </p>

            <button type="submit">Se connecter</button>
          </form>
        </div>
      )}
    </header>
  );
};

export default Header;
