import React, { useState } from "react";
import { BrowserRouter as Router, Route,Routes, Switch } from "react-router-dom";
import Header from "./components/Header";
import Home from "./components/Home";
import Voitures from "./components/Voitures";
import Footer from "./components/Footer";

const App = () => {
  const [chosenCars, setChosenCars] = useState([]);

  const handleChoose = (car) => {
    setChosenCars((prevChosenCars) => [...prevChosenCars, car]);
  };

  return (
    <Router>
      <div>
        <Header />

        <Routes>
          <Route path="/" exact>
            <Home onChoose={handleChoose} />
          </Route>
          <Route path="/voiture/:id">
            <Voitures cars={chosenCars} onChoose={handleChoose} />
          </Route>
        </Routes>

        <Footer />
      </div>
    </Router>
  );
};

export default App;
